package Tasks;

public class ForLoopTableOf2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=2;
		System.out.println("Table of 2 using for loop");
		for (int i=1; i<=10; i=i+1)
		{
			System.out.println(num +"*" +i+ "=" +num*i);
			
		}

	}

}
