package statementsAndLoops;

import java.util.Scanner;


public class ElseIfDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int num=13;
		
		if (num%2==0)
		{
			System.out.println("This is a good num");
		}
		else if(num>50)
		{
			System.out.println("This ia an OK num");
		}
		else {
			System.out.println("This is bad num");
		}
		
	}

}
