package statementsAndLoops;


/*
 * If else is a type of statement.
 * We use it to put constraints or conditions on out code.
 * 
 * If block of codes will execute only when the condition in if is statified.
 * otherwise else will execute.
 */

public class IfDemo1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		String mood="Happy";
		
		if(mood=="Happy")
		{
		System.out.println("I am Happy");
		}
		
		else
		{
		System.out.println("I am sad");
		}
	}

}
